// import logo from './logo.svg';
import "./App.css";

import { useState } from "react";

function App() {
  const getLocation = (e) => {
    const clickPosX = e.nativeEvent.offsetX;
    const clickPosY = e.nativeEvent.offsetY;
    // console.log(e.target.clientWidth);
    const clickPosXPercentage = clickPosX / e.target.clientWidth;
    const clickPosYPercentage = clickPosY / e.target.clientHeight;
    console.log(clickPosXPercentage * 100, clickPosYPercentage * 100);
  };

  return (
    <div
      className="app"
      onClick={(e) => {
        getLocation(e);
      }}
    >
      <div
        className="tag"
        style={{ position: "absolute", top: "100%", left: "100%" }}
      ></div>
    </div>
  );
}

export default App;
