import React, { useEffect } from "react";
import "./Tooltip.css";

function Tooltip({ children, title, TooltipState, setShowTooltip }) {
  console.log(TooltipState);

  //   if (TooltipState) {
  //     setTimeout(() => {
  //       setShowTooltip(false);
  //     }, 3000);
  //   }

  return (
    <div
      className={TooltipState ? `tooltip showTooltip` : "tooltip"}
      data-tool-tip={title}
    >
      <p className="prev">prev Bid</p>
      <p className="current">New Bid</p>
      <p className="next">next Bid</p>
    </div>
  );
}

export default Tooltip;
